import React, { useState } from "react";
import { Button, Row, Col } from "react-bootstrap";

const PEOPLE = [
    "Alan Turing",
    "Grace Hopper",
    "Ada Lovelace",
    "Charles Babbage",
    "Barbara Liskov",
    "Margaret Hamilton"
];

interface memberProps {
    setTeam: (newTeam: string[]) => void;
    team: string[];
    setAllOptions: (newAllOption: string[]) => void;
    allOptions: string[];
    option: string;
}

interface clearProps {
    setTeam: (newTeam: string[]) => void;
}

export function ChooseTeam(): JSX.Element {
    const [allOptions, setAllOptions] = useState<string[]>(PEOPLE);
    const [team, setTeam] = useState<string[]>([]);

    function setTeamOption(
        team: string[],
        allOptions: string[],
        option: string
    ) {
        if (!team.includes(option)) {
            setTeam([...team, option]);
            const newAllOptions = [...allOptions];
            const filtAllOptions = newAllOptions.filter(
                (optionit: string): boolean => optionit != option
            );
            setAllOptions([...filtAllOptions]);
        } else {
            setTeam([...team]);
            setAllOptions([...allOptions]);
        }
    }

    function restoreTeamOption() {
        setTeam([]);
        setAllOptions(PEOPLE);
    }

    function ChooseMember({
        team,
        allOptions,
        option
    }: memberProps): JSX.Element {
        return (
            <Button
                onClick={() => setTeamOption(team, allOptions, option)}
                size="sm"
            >
                {option}
            </Button>
        );
    }

    function ClearTeam(): JSX.Element {
        return <Button onClick={() => restoreTeamOption()}>Clear Team</Button>;
    }

    return (
        <div>
            <h3>Choose Team</h3>
            <Row>
                <Col>
                    {allOptions.map((option: string) => (
                        <div key={option} style={{ marginBottom: "4px" }}>
                            Add{" "}
                            <ChooseMember
                                setTeam={setTeam}
                                team={team}
                                setAllOptions={setAllOptions}
                                allOptions={allOptions}
                                option={option}
                            ></ChooseMember>
                        </div>
                    ))}
                </Col>
                <Col>
                    <strong>Team:</strong>
                    {team.map((member: string) => (
                        <li key={member}>{member}</li>
                    ))}
                    <ClearTeam></ClearTeam>
                </Col>
            </Row>
        </div>
    );
}
