import React, { useState } from "react";
import { Form } from "react-bootstrap";

export function EditMode(): JSX.Element {
    const [editMode, setEditMode] = useState<boolean>(false);
    const [boolStudent, setBoolStudent] = useState<boolean>(false);
    const [name, setName] = useState<string>("");

    function updateEditMode(editMode: boolean) {
        setEditMode(!editMode);
    }

    function updateName(event: React.ChangeEvent<HTMLInputElement>) {
        setName(event.target.value);
    }

    {
        if (editMode === false) {
            setName("Your Name");
            setBoolStudent(true);
        }
    }

    function OutputString(name: string, boolStudent: boolean): string {
        let ret = "";
        if (boolStudent === true) {
            ret = "is a student";
        } else {
            ret = "is not a student";
        }
        return ret;
    }

    return (
        <div>
            <h3>Edit Mode</h3>
            <Form.Check
                type="switch"
                id="editModeSwitch"
                label="Edit Mode"
                checked={editMode}
                onChange={() => updateEditMode(editMode)}
            />
            if (editMode === true)
            {
                <>
                    <Form.Group controlId="formName">
                        <Form.Label>Name:</Form.Label>
                        <Form.Control value={name} onChange={updateName} />
                    </Form.Group>
                    <div>{name + " " + OutputString(name, boolStudent)}</div>
                </>
            }
        </div>
    );
}
